# Add vagrant user.
useradd vagrant
echo "vagrant:vagrant" | chpasswd

# Add vagrant ssh keys
mkdir -p /home/vagrant/.ssh
chmod 700 /home/vagrant/.ssh
echo "ssh-rsa  vagrant insecure public key" >> /home/vagrant/.ssh/authorized_keys
echo "ssh-ed25519 AAAAC3NzaC1lZDI1NTE5AAAAIN1YdxBpNlzxDqfJyw/QKow1F+wvG9hXGoqiysfJOn5Y vagrant insecure public key" >> /home/vagrant/.ssh/authorized_keys

# Setup non-interactive shell to stop errors
# See: https://github.com/moby/moby/issues/27988
echo 'debconf debconf/frontend select Noninteractive' | debconf-set-selections

# Install sudo
apt-get update
apt-get install -y sudo

# Add vagrant user to sudoers.
echo "vagrant ALL=(ALL) NOPASSWD: ALL" > /etc/sudoers.d/vagrant